mapper.select:
select *
from member
where m_id = #{m_id}
and m_pw = #{m_pw}

mapper.update:
				update member set 	  m_name = #{m_name}
					                , m_tel = #{m_tel}
					                , m_area = #{m_area}
					                , m_field = #{m_field}
					                , m_meetingauth = #{m_meetingauth}
					                , m_masterauth = #{m_masterauth}
                where m_id = #{m_id}

mapper.select:
select *
from member NATURAL JOIN reginfo NATURAL JOIN post
where m_id = #{m_id}

mapper.delete:
delete from reginfo
where m_id = #{m_id}
and bt_num = #{bt_num}
and bc_num = #{bc_num}
and p_num = #{p_num}

mapper.select:
select *
from member NATURAL JOIN bookmark NATURAL JOIN post
where m_id = #{m_id}

mapper.update:
update bookmark set b_reg = 'Y'
where m_id = #{m_id}
and bt_num = #{bt_num}
and bc_num = #{bc_num}
and p_num = #{p_num}

mapper.delete:
delete from bookmark
where m_id = #{m_id}
and bt_num = #{bt_num}
and bc_num = #{bc_num}
and p_num = #{p_num}
